import svgPaths from "../imports/svg-lw4kwhgp6g";
import { useState } from "react";

interface SheetProps {
  onClose: () => void;
}

function TitleRow() {
  return (
    <div className="box-border content-stretch flex gap-[10px] items-center justify-center px-[4px] py-[8px] relative shrink-0" data-name="Title Row">
      <p className="font-['Inter:Semi_Bold',sans-serif] font-semibold leading-[normal] not-italic relative text-[20px] text-[rgba(42,51,61,0.96)]">О приложении</p>
    </div>
  );
}

function IconXmark() {
  return (
    <div className="relative shrink-0 size-[24px] select-none" data-name="Icon Xmark">
      <svg className="block size-full" fill="none" preserveAspectRatio="none" viewBox="0 0 24 24">
        <g id="Icon Xmark">
          <path d={svgPaths.p30b24840} fill="var(--fill-0, #2A333D)" fillOpacity="0.8" id="Union" />
        </g>
      </svg>
    </div>
  );
}

function IconButton({ onClick }: { onClick: () => void }) {
  return (
    <button 
      onClick={onClick}
      className="bg-[rgba(42,51,61,0.08)] box-border content-stretch cursor-pointer flex flex-col gap-[8px] items-center justify-center overflow-clip p-[8px] relative rounded-[9999px] shrink-0 size-[40px] transition-all duration-[160ms] ease-out active:opacity-[0.64] after:absolute after:inset-0 after:rounded-[9999px] after:bg-[rgba(42,51,61,0.08)] after:opacity-0 hover:after:opacity-100 active:after:opacity-100 after:transition-opacity after:duration-[160ms] after:ease-out" 
      data-name="Icon Button"
    >
      <IconXmark />
    </button>
  );
}

function Header({ onClose }: { onClose: () => void }) {
  return (
    <div className="backdrop-blur-[32px] backdrop-filter bg-[rgba(252,254,255,0)] relative shrink-0 w-full z-[2]" data-name="Header">
      <div className="size-full">
        <div className="box-border content-stretch flex gap-[16px] items-start justify-between pl-[20px] pr-[16px] py-[16px] relative w-full">
          <TitleRow />
          <IconButton onClick={onClose} />
        </div>
      </div>
    </div>
  );
}

