function Frame2() {
  return (
    <div className="basis-0 content-stretch flex flex-col gap-[32px] grow items-center justify-center min-h-px min-w-px relative shrink-0 w-full">
      <AppIconRegular />
      <TextLogo />
    </div>
  );
}

function Frame3() {
  return (
    <div className="basis-0 grow min-h-px min-w-px relative shrink-0">
      <div className="size-full">
        <div className="box-border content-stretch flex flex-col font-['Inter:Regular',sans-serif] font-normal gap-[4px] items-start not-italic px-[4px] py-0 relative w-full">
          <p className="leading-[21px] relative shrink-0 text-[16px] text-[rgba(42,51,61,0.96)] text-nowrap whitespace-pre">Разрешить сбор метрик</p>
          <p className="leading-[19px] min-w-full relative shrink-0 text-[14px] text-[rgba(42,51,61,0.8)] w-[min-content]">Так вы поможете нам учитывать особенности вашего окружения</p>
        </div>
      </div>
    </div>
  );
}

function Knob({ isOn }: { isOn: boolean }) {
  return (
    <div 
      className={`aspect-[28/28] bg-[#fcfeff] h-full rounded-[99999px] shrink-0 z-[1] transition-transform duration-[160ms] ease-out ${
        isOn ? 'translate-x-[17px]' : 'translate-x-0'
      }`}
      data-name="Knob"
    />
  );
}

function ToggleSwitch() {
  const [isOn, setIsOn] = useState(true);

  const handleToggle = () => {
    setIsOn(!isOn);
  };

  return (
    <div 
      onClick={handleToggle}
      className={`h-[28px] relative rounded-[9999px] shrink-0 w-[45px] cursor-pointer select-none overflow-hidden transition-all duration-[160ms] ease-out after:content-[''] after:absolute after:inset-0 after:rounded-[9999px] after:opacity-0 after:transition-all after:duration-[160ms] after:ease-out hover:after:opacity-100 active:after:opacity-100 active:opacity-64 ${
        isOn 
          ? 'bg-[#4580f6] after:bg-[rgba(252,254,255,0.16)]' 
          : 'bg-[rgba(42,51,61,0.16)] after:bg-[rgba(42,51,61,0.08)]'
      }`}
      data-name="Toggle Switch"
    >
      <div className="box-border content-stretch flex h-[28px] isolate items-center overflow-clip p-[3px] relative rounded-[inherit] w-[45px] z-10">
        <Knob isOn={isOn} />
      </div>
    </div>
  );
}

function Item() {
  return (
    <div className="bg-[#fcfeff] box-border content-stretch flex gap-[8px] items-start overflow-clip p-[16px] relative rounded-[12px] shrink-0 w-full" data-name="Item">
      <Frame3 />
      <ToggleSwitch />
    </div>
  );
}

function Frame1() {
  return (
    <div className="content-stretch flex flex-wrap font-['Inter:Regular',sans-serif] font-normal gap-[16px] items-start justify-center leading-[23px] not-italic relative shrink-0 text-[16px] text-[rgba(42,51,61,0.64)]">
      <p className="-webkit-box overflow-ellipsis overflow-hidden relative shrink-0 whitespace-nowrap">Компания «Актив»</p>
      <p className="-webkit-box overflow-ellipsis overflow-hidden relative shrink-0 whitespace-nowrap">Версия 0.1.0</p>
    </div>
  );
}

function Content() {
  return (
    <div className="basis-0 grow min-h-px min-w-px relative shrink-0 w-full" data-name="Content">
      <div className="flex flex-col items-center justify-center overflow-clip rounded-[inherit] size-full px-[24px]">
        <div className="box-border content-stretch flex flex-col gap-[32px] items-center justify-center py-[32px] relative h-full w-full max-w-[384px]">
          <Frame2 />
          <Item />
          <Frame1 />
        </div>
      </div>
    </div>
  );
}

function ScrollArea() {
  return (
    <div className="basis-0 content-stretch flex flex-col grow items-start min-h-px min-w-px relative shrink-0 w-full z-[1]" data-name="Scroll Area" style={{ scrollbarGutter: 'stable both-edges', scrollbarColor: 'gray transparent' }}>
      <Content />
    </div>
  );
}

export default function Sheet({ onClose }: SheetProps) {
  return (
    <div className="content-stretch flex flex-col isolate items-start overflow-clip relative rounded-[24px] size-full" data-name="Sheet" style={{ backgroundImage: "linear-gradient(90deg, rgba(42, 51, 61, 0.08) 0%, rgba(42, 51, 61, 0.08) 100%), linear-gradient(90deg, rgb(252, 254, 255) 0%, rgb(252, 254, 255) 100%)" }}>
      <Header onClose={onClose} />
      <ScrollArea />
    </div>
  );
}